package in.co.rays.basics;

public class CalculatorIfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int m = 45;
		int n = 70;
		char Op = '+';
		
	if(Op == '+') {
	System.out.println(m+n);
	}
	else if(Op=='-') {
		System.out.println(m-n);
	}
	else if(Op=='*') {
	    System.out.println(m*n);
	}
	else if(Op=='/') {
		System.out.println(m/n);
		
		
	}

	}

}
