package in.co.rays.basics;

public class CalculatorSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 10;
		int num2 = 20;
		char Operation = '+';
		
		switch (Operation) {
		case'+':
			System.out.println(num1+num2 );
			break;
		case'-' :
			System.out.println(num1-num2);
			break;
		case'*':
			System.out.println(num1*num2);
			break;
		case'/':
			System.out.println(num1/num2);
			break;
			
		}
	}
}
			
			
			
			
			
			