package in.co.rays.basics;

public class Precedence {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int a = 1; //initialization
     int b = 2;
     int c = 3;
     int d = 4;
     int total = a+b*c/d;
     int value = a-b+c*d;
    
     System.out.println(total);
     System.out.println(value); 
	}

}
